#define LOCAL_SIZE_X 16u
#define LOCAL_SIZE_Y 8u
#define LOCAL_SIZE_Z 1u
layout (local_size_x = LOCAL_SIZE_X, local_size_y = LOCAL_SIZE_Y, local_size_z = LOCAL_SIZE_Z) in;
layout(std140) uniform CCConst {
  vec4 cc_nearFar;
  vec4 cc_viewPort;
  vec4 cc_workGroup;
  mat4 cc_matView;
  mat4 cc_matProjInv;
};
