vec4 screen2Eye(vec4 coord) {
  vec3 ndc = vec3(
    2.0 * (coord.x - cc_viewPort.x) / cc_viewPort.z - 1.0,
    2.0 * (coord.y - cc_viewPort.y) / cc_viewPort.w - 1.0,
    2.0 * coord.z - 1.0);
  CC_HANDLE_SAMPLE_NDC_FLIP_STATIC(ndc.y);
